import React from 'react'

function Experience() {
    const [experienceFields, setFormFields] = useState([
        {company: '', role: '', from: '', to: ''}
    ])

    const handleFormChange = (event, index) => {
        let data = [...experienceFields]
        data[index][event.target.name] = event.target.value
        setFormFields(data)
    }

    const addEducation = () => {
        let object = {
            company: '',
            role: '',
            from: '',
            to: ''
        }

        setFormFields([...experienceFields, object])

    }

    const removeEducation = (index) => {
        let data = [...experienceFields];
        data.splice(index,1)
        setFormFields(data)
    }

    return (
        <div className='eductation-container'>
            <form>
                {experienceFields.map((form, index) => {
                    return (
                        <div key={index}>
                            <input
                                name='company'
                                placeholder='Company'
                                onChange={event => handleFormChange(event, index)}
                                value={form.company}
                            />
                            <input
                                name='role'
                                placeholder='Role'
                                onChange={event => handleFormChange(event, index)}
                                value={form.role}
                            />
                            <input
                                name='from'
                                placeholder='From'
                                onChange={event => handleFormChange(event, index)}
                                value={form.from}
                            />
                            <input
                                name='to'
                                placeholder='To'
                                onChange={event => handleFormChange(event, index)}
                                value={form.to}
                            />
                            <button onClick={removeEducation}>Remove</button>
                    </div>

                    )
                })}

            </form>
                <button onClick={addEducation}>Add</button>
        </div>
    )
}

export default Experience
