import React, { useState } from 'react'
let edcount = 0
const add = document.getElementById('add')
function Education({ eductationFields, setFormFields} ) {

    const handleFormChange = (event, index) => {
        let data = [...eductationFields]
        data[index][event.target.name] = event.target.value
        setFormFields(data)
    }

    const addEducation = () => {

        if(edcount < 1) {
        let object = {
            school: '',
            study: '',
            from: '',
            to: ''
        }
        setFormFields([...eductationFields, object])}

        else {add.setAttribute('disabled')}

        edcount += 1

    }

    const removeEducation = (index) => {
        edcount -= 1
        let data = [...eductationFields];
        data.splice(index,1)
        setFormFields(data)
        add.removeAttribute('disabled')
    }

    return (
        <div className='education'>
            <form>
                {eductationFields.map((form, index) => {
                    return (
                        <div className='education-container' key={index}>
                            <input
                                name='school'
                                placeholder='School/University'
                                onChange={event => handleFormChange(event, index)}
                                value={form.school}
                            />
                            <input
                                name='study'
                                placeholder='Area of Study'
                                onChange={event => handleFormChange(event, index)}
                                value={form.study}
                            />
                            <input
                                name='from'
                                placeholder='From'
                                onChange={event => handleFormChange(event, index)}
                                value={form.from}
                            />
                            <input
                                name='to'
                                placeholder='To'
                                onChange={event => handleFormChange(event, index)}
                                value={form.to}
                            />
                            <button type='button' onClick={removeEducation}>Remove</button>
                    </div>

                    )
                })}

            </form>
                <button id='add' onClick={addEducation}>Add</button>
        </div>
    )
}

export default Education
